        style={style || { display: 'block' }}
        data-ad-client="ca-pub-XXXXXXXXXXXXXXXX" // Ïã§Ï†ú Ïï†ÎìúÏÑºÏä§ IDÎ°ú ÍµêÏ≤¥
        data-ad-slot={slot}
        data-ad-format="auto"
        data-full-width-responsive="true"
